INSERT INTO `mallbuilder_mail_mod` VALUES ('1', '[给用户] 邮箱验证', '请激活您在[weburl_name]账户 ', '&lt;table style=&quot;background-color:#F3F3F2;font-size:12px;margin:auto;&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; width=&quot;650&quot;&gt;\r\n	&lt;tbody&gt;\r\n		&lt;tr&gt;\r\n			&lt;td&gt;\r\n				&lt;table style=&quot;margin:20px 20px 15px;&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; width=&quot;100%&quot;&gt;\r\n					&lt;tbody&gt;\r\n						&lt;tr&gt;\r\n							&lt;td&gt;\r\n								&lt;img style=&quot;border:none;&quot; src=&quot;http://localhost/mallbuilder/image/logo.png&quot; height=&quot;31&quot; /&gt; \r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n					&lt;/tbody&gt;\r\n				&lt;/table&gt;\r\n				&lt;table style=&quot;width:610px;margin:0 auto;background-color:#FFF;&quot;&gt;\r\n					&lt;tbody&gt;\r\n						&lt;tr&gt;\r\n							&lt;td&gt;\r\n								&lt;div style=&quot;margin:25px 15px 15px;font-size:14px;&quot;&gt;\r\n									&lt;span style=&quot;font-weight:bold;line-height:30px;&quot;&gt;亲爱的用户您好：&lt;/span&gt;&lt;br /&gt;\r\n&lt;span style=&quot;line-height:30px;&quot;&gt; 您于 [time] &amp;nbsp;申请邮箱验证。&lt;br /&gt;\r\n验证码:&lt;br /&gt;\r\n[link]&lt;br /&gt;\r\n若您没有申请过邮箱验证 ，请发邮件至：&lt;a target=&quot;_blank&quot; href=&quot;mailto:[weburl_email]&quot;&gt;[weburl_email]&lt;/a&gt; 或致电：[weburl_tel]。 &lt;/span&gt;&lt;br /&gt;\r\n									&lt;div&gt;\r\n										本邮件由[weburl_name]系统自动发出，请勿直接回复！&lt;br /&gt;\r\n在购物中遇到任何问题，请点击 &lt;a href=&quot;[weburl_url]/help.php&quot; target=&quot;_blank&quot;&gt;帮助中心&lt;/a&gt;。&lt;br /&gt;\r\n如果您有任何疑问或建议，请 &lt;a target=&quot;_blank&quot; href=&quot;[weburl_url]/aboutus.php?msg=1&quot;&gt;联系我们&lt;/a&gt;。&lt;br /&gt;\r\n[weburl_desc]\r\n									&lt;/div&gt;\r\n								&lt;/div&gt;\r\n							&lt;/td&gt;\r\n						&lt;/tr&gt;\r\n					&lt;/tbody&gt;\r\n				&lt;/table&gt;\r\n			&lt;/td&gt;\r\n		&lt;/tr&gt;\r\n	&lt;/tbody&gt;\r\n&lt;/table&gt;', 'active', '0', '0');
INSERT INTO `mallbuilder_mail_mod` VALUES ('2', '[给用户] 欢迎邮件', '感谢您注册[weburl_name]', '<table style=\"background-color:#F3F3F2;font-size:12px;margin:auto;\" cellpadding=\"0\" cellspacing=\"0\" width=\"650\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<table style=\"margin:20px 20px 15px;\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\">\r\n					<tbody>\r\n						<tr>\r\n							<td>\r\n								<img style=\"border:none;\" src=\"http://localhost/mallbuilder/image/logo.png\" height=\"31\" /> \r\n							</td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n				<table style=\"width:610px;margin:0 auto;background-color:#FFF;line-height:30px;\">\r\n					<tbody>\r\n						<tr>\r\n							<td>\r\n								<div style=\"margin:25px 15px 15px;font-size:14px;line-height:30px;\">\r\n									<span style=\"font-weight:bold;line-height:30px;\">亲爱的用户您好：</span><br />\r\n<span style=\"line-height:30px;\"> 感谢您注册[weburl_name]<br />\r\n我们将为您提供最贴心的服务，祝您购物愉快！<br />\r\n您的用户名：[member_name]&nbsp;&nbsp;&nbsp;&nbsp;您的注册邮箱：[member_email]\r\n                                    邮件中包含您的个人信息，建议您保管好本邮件！如您登陆时<a target=\"_blank\" href=\"[weburl_url]/lostpass.php\">忘记密码</a>。 </span> \r\n									<div style=\"line-height:30px;border-top:1px dotted #DDD;margin-top:10px;padding-top:10px;\">\r\n										本邮件由[weburl_name]系统自动发出，请勿直接回复！<br />\r\n在购物中遇到任何问题，请点击 <a href=\"[weburl_url]/help.php\" target=\"_blank\">帮助中心</a>。<br />\r\n如果您有任何疑问或建议，请 <a target=\"_blank\" href=\"[weburl_url]/aboutus.php?msg=1\">联系我们</a>。<br />\r\n[weburl_desc]\r\n									</div>\r\n								</div>\r\n							</td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 'register', '0', '0');
INSERT INTO `mallbuilder_mail_mod` VALUES ('3', '[给用户] 找回密码', '[weburl_name]找回密码', '<table style=\"background-color:#F3F3F2;font-size:12px;margin:auto;\" cellpadding=\"0\" cellspacing=\"0\" width=\"650\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<table style=\"margin:20px 20px 15px;\" cellpadding=\"0\" cellspacing=\"0\" width=\"100%\">\r\n					<tbody>\r\n						<tr>\r\n							<td>\r\n								<img style=\"border:none;\" src=\"http://localhost/mallbuilder/image/logo.png\" height=\"31\" /> \r\n							</td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n				<table style=\"width:610px;margin:0 auto;background-color:#FFF;\">\r\n					<tbody>\r\n						<tr>\r\n							<td>\r\n								<div style=\"margin:25px 15px 15px;font-size:14px;\">\r\n									<span style=\"font-weight:bold;line-height:30px;\">亲爱的用户您好：</span><br />\r\n<span style=\"line-height:30px;\"> 您于 [time] &nbsp;申请找回密码。<br />\r\n将链接复制到浏览器地址栏访问:<br />\r\n[link]<br />\r\n若您没有申请过找回密码 ，请发邮件至：<a target=\"_blank\" href=\"mailto:[weburl_email]\">[weburl_email]</a> 或致电：[weburl_tel]。 </span><br />\r\n<div style=\"line-height:30px; border-top:1px dotted #DDD; margin-top:10px; padding-top:10px;\">本邮件由[weburl_name]系统自动发出，请勿直接回复！<br />\r\n在购物中遇到任何问题，请点击 <a href=\"[weburl_url]/help.php\" target=\"_blank\">帮助中心</a>。<br />\r\n如果您有任何疑问或建议，请 <a target=\"_blank\" href=\"[weburl_url]/aboutus.php?msg=1\">联系我们</a>。<br />\r\n[weburl_desc] </div> \r\n								</div>\r\n							</td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 'find_pwd', '0', '0');