--
-- 表的结构 `mallbuilder_news`
--

DROP TABLE IF EXISTS `mallbuilder_news`;
CREATE TABLE IF NOT EXISTS `mallbuilder_news` (
  `nid` int(11) NOT NULL AUTO_INCREMENT,
  `classid` smallint(6) NOT NULL DEFAULT '1' COMMENT '类别ID',
  `title` varchar(200) NOT NULL COMMENT '标题',
  `ftitle` varchar(120) NOT NULL COMMENT '副标题',
  `keyboard` varchar(100) NOT NULL COMMENT '关键字',
  `titleurl` varchar(200) NOT NULL DEFAULT '0' COMMENT '外部链接',
  `isrec` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否推荐 0否1是',
  `istop` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否头条 0否1是',
  `ispass` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否审批 0否1是',
  `firsttitle` tinyint(1) NOT NULL DEFAULT '0' COMMENT '置顶',
  `onclick` int(11) NOT NULL DEFAULT '0' COMMENT '点击率',
  `titlefont` varchar(100) NOT NULL COMMENT '标题样式',
  `uid` int(11) NOT NULL COMMENT '用户ID',
  `uptime` int(12) NOT NULL COMMENT '新闻发布时间',
  `smalltext` varchar(255) NOT NULL COMMENT '内容简介',
  `writer` varchar(50) NOT NULL COMMENT '作者',
  `source` varchar(100) NOT NULL COMMENT '来源',
  `titlepic` varchar(200) NOT NULL COMMENT '标题图片',
  `ispic` tinyint(1) NOT NULL DEFAULT '0' COMMENT '有无图片 0否1有',
  `isgid` tinyint(1) NOT NULL DEFAULT '0' COMMENT '阅读权限 0游客',
  `ispl` tinyint(1) NOT NULL COMMENT '是否关闭评论 1关',
  `userfen` smallint(6) NOT NULL COMMENT '查看扣除点数',
  `newstempid` varchar(40) NOT NULL COMMENT '内容模板',
  `pagenum` int(11) NOT NULL DEFAULT '0' COMMENT '分页',
  `lastedittime` int(12) NOT NULL,
  `vote` char(255) NOT NULL DEFAULT '0',
  `special` char(255) NOT NULL DEFAULT '0',
  `imgs_url` text NOT NULL,
  `videos_url` text NOT NULL,
  `admin` varchar(50) DEFAULT NULL,
  PRIMARY KEY (`nid`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 AUTO_INCREMENT=10 ;

--
-- 导出表中的数据 `mallbuilder_news`
--

INSERT INTO `mallbuilder_news` (`nid`, `classid`, `title`, `ftitle`, `keyboard`, `titleurl`, `isrec`, `istop`, `ispass`, `firsttitle`, `onclick`, `titlefont`, `uid`, `uptime`, `smalltext`, `writer`, `source`, `titlepic`, `ispic`, `isgid`, `ispl`, `userfen`, `newstempid`, `pagenum`, `lastedittime`, `vote`, `special`, `imgs_url`, `videos_url`, `admin`) VALUES
(1, 1, '周鸿祎内部信：海里的鲨鱼不会到陆地上和狮子去拼', '周鸿祎内部信：海里的鲨鱼不会到陆地上和狮子去拼', '', '', 1, 0, 1, 0, 88, '', 0, 1400589942, '2014 开始，360 在新领域并未有太多声音，经历前段时间的"闭关"思考，日前周鸿祎发布内部公开信，告诫员工勿盲目把 360 看成互联网巨头，并表示把安全这件事，做得更深、更透，把擅长的安全做到极致', '', '', '', 0, 0, 0, 0, '默认新闻内容模板', 0, 1405141480, '1,2,', ',', '', '', 'admin'),
(2, 1, '站长扶持计划，助您实现站长的创业梦想', '站长扶持计划，助您实现站长的创业梦想', '', '', 0, 0, 1, 0, 99, '', 0, 1400589998, '据调查，大量有思想，有追求，有资源，懂运营的站长，因经济条件受限，又缺乏一个款好的建站软件，以及专业的互联网服务团队支持，很难实现自己的建站梦想，也有一些人勉强建立了自己的网站，但是在运营过程中，孤军', '', '', 'http://democn.mall-builder.com/uploadfile/news/2014/05/20/1400590019.jpg', 1, 0, 0, 0, '默认新闻内容模板', 0, 1405141489, ',', ',', '', '', 'admin'),
(3, 1, '24种值得你掌握的跨境电商工具', '24种值得你掌握的跨境电商工具', '', '', 0, 1, 1, 0, 47, '', 0, 1400806941, '​​一元云购市场推广者要学会三点：\r\n\r\n1. 适应新的推广方法标准。\r\n\r\n2. 学会合作沟通，因为很多同行其实认识的不一定比你少。\r\n\r\n3. 学会使用新工具让你的工作更加顺畅。', '', '', 'http://democn.mall-builder.com/uploadfile/news/2014/05/23/1400807182.jpg', 1, 0, 0, 0, '默认新闻内容模板', 0, 1405141471, ',', ',', '', '', 'admin'),
(4, 1, '京东IPO：今日资本获100倍回报 刘强东身价达61亿美元', '京东IPO：今日资本获100倍回报 刘强东身价达61亿美元', '', '', 0, 1, 1, 0, 54, '', 0, 1400808008, '北京时间5月22日晚上11点，中国最大的自营B2C电子商务公司——京东(jd.com）正式在美国纳斯达克挂牌上市，开盘价21.75美元，较19美元的发行价大涨14.5%，按此计算，京东市值为297.3', '', '', 'http://democn.mall-builder.com/uploadfile/news/2014/05/23/1400807430.jpg', 1, 0, 0, 0, '默认新闻内容模板', 0, 1405141461, ',', ',', '', '', 'admin'),
(5, 1, '规则之外的微信：别只盯着裁判员！', '规则之外的微信：别只盯着裁判员！', '', '', 0, 0, 1, 0, 26, '', 0, 1400808382, '\r\n规则之外的微信：别只盯着裁判员！\r\n2014-05-23 06:55:59 作者：郭静 标签： 微信 投稿 产品 平台 评论：1 字号： S M L\r\n\r\n任何“主场”的裁判员都不会因观众出现不和谐的声音', '', '', 'http://democn.mall-builder.com/uploadfile/news/2014/05/23/1400807700.jpg', 1, 0, 0, 0, '默认新闻内容模板', 0, 1400808382, ',', ',', '', '', 'admin'),
(6, 1, '天使把脉创业：徐小平投“三痛”', '天使把脉创业：徐小平投“三痛”', '', '', 0, 0, 1, 0, 59, '', 0, 1400808611, '辛辛苦苦开始了创业，好不容易见到了天使投资人，如何去打动明星投资人？如何能拿到那一笔“救命”钱？看徐小平、雷军这样说。', '', '', 'http://democn.mall-builder.com/uploadfile/news/2014/05/23/1400808589.jpg', 1, 0, 0, 0, '默认新闻内容模板', 0, 1405141454, ',', ',', '', '', 'admin'),
(7, 1, '免费WIFI有风险 蹭网小心被“钓鱼”转账', '免费WIFI有风险 蹭网小心被“钓鱼”转账', '', '', 0, 0, 1, 0, 50, '', 0, 1400808842, '公交车上，很多人低头玩手机；地铁上，低头族也是非常多；甚至走在路上都要玩手机，这就是如今智能手机和无线网络对人们生活的影响。现如今，昆明许多公共场所也都向市民提供免费WIFI，许多市民在喝着咖啡惬意地', '', '', 'http://democn.mall-builder.com/uploadfile/news/2014/05/23/1400808823.jpg', 1, 0, 0, 0, '默认新闻内容模板', 0, 1405141446, ',', ',', '', '', 'admin'),
(8, 1, '马云个人收购绿城控股49%股份 宋卫平：球队还姓宋', '马云个人收购绿城控股49%股份 宋卫平：球队还姓宋', '', '', 0, 0, 1, 0, 131, '', 0, 1400809016, '昨天下午，网上一则微博激起千层浪，名为“财经国家周刊”的微博发布消息称：“绿城中国股权将出现大变化。1，宋卫平退出上市公司，专心做绿城建设。2，九龙仓成为上市公司最大股东。3，同日停牌的融创中国股权比', '', '', 'http://democn.mall-builder.com/uploadfile/news/2014/05/23/1400808996.jpg', 1, 0, 0, 0, '默认新闻内容模板', 0, 1405141438, ',', ',', '', '', 'admin'),
(9, 1, '独家分析：电商上市潮将重塑社会商业模型', '独家分析：电商上市潮将重塑社会商业模型', '', '', 0, 0, 1, 0, 296, '', 0, 1400809129, '【亿邦动力网讯】5月22日，京东今日成功在美IPO，获得资本市场力捧……。在京东之前，有国内最大的化妆品垂直电商聚美优品登陆纽交所，在京东之后，则有更凶猛的电商巨无霸阿里巴巴整体上市。', '', '', 'http://democn.mall-builder.com/uploadfile/news/2014/05/23/1400809202.jpg', 1, 0, 0, 0, '默认新闻内容模板', 0, 1405141431, ',', ',', '', '', 'admin');

